// Future work: add more options and the ability to allocate
// a certain amount of the budget to each expense
import axios from "axios";
import optionsData from ".Services/options.json"
const url =
  "https://my-json-server.typicode.com/kellybuchanan/WebDev-Spring2021";
export const createOption = async (label, value) => {
  return axios
    .post(`${url}/options`, {
      label,
      value
    })
    .then((response) => {
      console.log("POST response: ", response);
    })
    .catch((err) => {
      console.log("POST error: ", err);
    });
};

export const getAllOptions = () => {
  return axios
    .get('src/Services/options.json')
    .then((response) => {
        const data = optionsData;
      if (Array.isArray(data)) {
        return data; // If it's already an array, return it
      } else if (typeof data === "object") {
        return Object.values(data); // Convert object values to an array
      } else {
        return []; // Return an empty array if data is neither an array nor object
      }
    })
    .catch((err) => {
      console.log("GET Error: ", err);
    });
};
